// Scroll Navbar
window.onscroll = function () {
  var nav_item = document.getElementsByClassName("nav-item");
  if (document.body.scrollTop > 80 || document.documentElement.scrollTop > 80) {
    //document.getElementById("header_ahref").style.padding = "0px";
    for (let i = 0; i < nav_item.length; i++) {
      nav_item[i].style.padding = "8px 0px";
    }
    document.getElementById("switchPic").style.padding = "0px";
    document.getElementById("header_ahref").style.width = "13%";
  } else {
    //document.getElementById("header_ahref").style.padding = "0px";
    for (let i = 0; i < nav_item.length; i++) {
      nav_item[i].style.padding = "15px 0px";
    }
    if ($(window).width() > 960) {
      document.getElementById("switchPic").style.padding = "5px";
    }
    else {
      document.getElementById("switchPic").style.padding = "0px";
    }
    document.getElementById("header_ahref").style.width = "16%";
  }
};

//Hide responsive Navbar
$('#navbarResponsive a').on('click', function(){
  if ($(window).width() < 992) {
    $('.navbar-toggler').click();
  }
});

// Scroll to top button

const backToTopBtn = document.querySelector("#back-to-top");
window.addEventListener("scroll", scrollFunction2);

function scrollFunction2() {
  backToTopBtn.style.display = "block";
  if (window.pageYOffset > 10) {
    if (!backToTopBtn.classList.contains("btnEntrance")) {
      backToTopBtn.classList.remove("btnExit");
      backToTopBtn.classList.add("btnEntrance");
      backToTopBtn.style.display = "block";
    }
  } else {
    if (backToTopBtn.classList.contains("btnEntrance")) {
      backToTopBtn.classList.remove("btnEntrance");
      backToTopBtn.classList.add("btnExit");
      setTimeout(function () {
        backToTopBtn.style.display = "none";
      }, 250);
    }
  }
}

backToTopBtn.addEventListener("click", backToTop);

function backToTop() {
  window.scrollTo(0, 0);
}

// Services Modals
function openServicesModal1() {
  document.getElementById("servicesModal1").style.display = "block";
}
function closeServicesModal1() {
  document.getElementById("servicesModal1").style.display = "none";
}
var slideIndex = 1;
showServicesSlides1(slideIndex);
function plusServicesSlides1(n) {
  showServicesSlides1((slideIndex += n));
}
function currentServicesSlide1(n) {
  showServicesSlides1((slideIndex = n));
}
function showServicesSlides1(n) {
  var i;
  var slides = document.getElementsByClassName("servicesSlides1");
  var dots = document.getElementsByClassName("demo");
  if (n > slides.length) {
    slideIndex = 1;
  }
  if (n < 1) {
    slideIndex = slides.length;
  }
  for (i = 0; i < slides.length; i++) {
    slides[i].style.display = "none";
  }
  for (i = 0; i < dots.length; i++) {
    dots[i].className = dots[i].className.replace(" active", "");
  }
  slides[slideIndex - 1].style.display = "block";
  dots[slideIndex - 1].className += " active";
}

